import './App.css';
import NavBar from './NavBar';
import Home from './Home';
import { Route, Switch } from 'react-router-dom';
import Tasks from './Tasks';

function App() {
	const linkStyles = {
		margin: '0 6px 6px',
		background: 'black',
		textDecoration: 'none',
		color: '#023047',
	};

	return (
		<div className="App" style={linkStyles}>
			<NavBar />
			<Switch>
				<Route exact path="/home">
					<Home />
				</Route>
				<Route path="/tasks">
					<Tasks />
				</Route>
			</Switch>
		</div>
	);
}

export default App;
