		<div
			style={
				checked
					? {
							backgroundColor: 'grey',
					  }
					: {}
			}
			id={task.id}
			className="task-card"
		>
			<li>{task['task_item']}</li>
			<label className="checkbox-container">
				<input
					id="checkbox"
					type="checkbox"
					onChange={handleTask}
				></input>
				<span className="checkmark"></span>
			</label>
		</div>
	);
};

